sys_script_include.script:
var ValidateCNPJ = Class.create();
ValidateCNPJ.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	ValidateCNPJ: function() {

		var cnpj = this.getParameter('sysparm_toValidate').toString();
		var response = '';

		var regularExpression_cnpj = /^\d\d\.\d{3}\.\d{3}\/\d{4}-\d\d$/; //formato 88.888.888/8888-88
		var regularExpression_lettersOnly = /[A-z]/g;
		var regularExpression_notNumbers = /\D/g;

		if (!regularExpression_cnpj.test(cnpj)) {
			cnpj = cnpj.replace(regularExpression_notNumbers, '');

			if (cnpj.length != 14 || regularExpression_lettersOnly.test(cnpj)){
				response = "CNPJ no formato incorreto, por favor insira um cnpj com 14 dígitos";
			}else {
				response = cnpj.substring(0,2) + "." + cnpj.substring(2,5) + "." + cnpj.substring(5,8) + "/" + cnpj.substring(8,12) + "-" + cnpj.substring(12,14);

			}
		}
		//Digito de Verificacao CNPJ
		var valorVerificar = 0;
		var dv = 0;
		var pesos = [5,6,7,8,9,2,3,4,5,6,7,8,9];
		if(response == ''){
			valorVerificar = cnpj.replace(/\D/g,'');
		}else if(response.length < 20){
			valorVerificar = response.replace(/\D/g,'');
		}else{
			return response;
		}

		for (var i = 0; i < 12 ; i++){
		  dv = dv + pesos[i+1]*parseInt(valorVerificar[i]);
		}
		dv = dv %= 11;
		if(dv==10){dv=0;}

		if(valorVerificar[12] != dv){
			response = "CNPJ inválido, por favor verifique o número.";
		}else{
			dv = 0;
			for (var i = 0; i < 13 ; i++){
				dv = dv + pesos[i]*parseInt(valorVerificar[i]);
			}
			dv = dv %= 11;
			if(dv==10){dv=0;}

			if(valorVerificar[13] != dv){
				response = "CNPJ inválido, por favor verifique o número.";
			}
		}

		return response;
	},
    type: 'ValidateCNPJ'
});
